        serialNumber: 'SK004X2D',
        status: 'available',
        batteryLevel: 95,
        location: { lat: 37.7549, lng: -122.4394 },
        flightHours: 312.1,
        lastMaintenance: '2024-01-25',
        maxFlightTime: 35,
