              boxShadow: reportType === 'detailed' ? '0 20px 25px -5px rgba(249, 115, 22, 0.1), 0 10px 10px -5px rgba(249, 115, 22, 0.04)' : '',
              borderWidth: reportType === 'detailed' ? '2px' : '1px',
