src/components/monitoring/LiveMonitoring.content:
import React, { useState, useEffect } from 'react';
import { useDrone } from '../../contexts/DroneContext';
import MissionCard from './MissionCard';
import MapView from './MapView';
import { Play, Pause, Square, RotateCcw } from 'lucide-react';

const LiveMonitoring: React.FC = () => {
  const { missions, updateMission, drones } = useDrone();
  const [selectedMission, setSelectedMission] = useState<string | null>(null);

  const activeMissions = missions.filter(m => m.status === 'in-progress' || m.status === 'paused');
  const selectedMissionData = selectedMission 
    ? missions.find(m => m.id === selectedMission)
    : activeMissions[0];

  const handleMissionControl = (missionId: string, action: 'pause' | 'resume' | 'abort') => {
    const mission = missions.find(m => m.id === missionId);
    if (!mission) return;

    switch (action) {
      case 'pause':
        updateMission(missionId, { status: 'paused' });
        break;
      case 'resume':
        updateMission(missionId, { status: 'in-progress' });
        break;
      case 'abort':
        updateMission(missionId, { status: 'aborted', progress: 0 });
        break;
    }
  };

  return (
    <div className="space-y-6">
      <div className="flex justify-between items-center">
        <h2 className="text-2xl font-bold text-white">Live Mission Monitoring</h2>
        <div className="flex items-center gap-2">
          <span className="text-gray-400 text-sm">
            {activeMissions.length} Active Mission{activeMissions.length !== 1 ? 's' : ''}
          </span>
        </div>
      </div>

      <div className="grid grid-cols-1 lg:grid-cols-3 gap-6">
        {/* Mission List */}
        <div className="space-y-4">
          <div className="flex justify-between items-center mb-4">
            <h3 className="text-lg font-medium text-white">Mission Queue</h3>
            <p className="text-sm text-gray-400">Active and pending missions</p>
          </div>
          {activeMissions.length === 0 ? (
            <div className="bg-gray-800 rounded-lg border border-gray-700 p-6 text-center">
              <p className="text-gray-400">No active missions</p>
            </div>
          ) : (
            activeMissions.map((mission) => (
              <MissionCard
                key={mission.id}
                mission={mission}
                isSelected={selectedMission === mission.id}
                onSelect={() => setSelectedMission(mission.id)}
                onControl={handleMissionControl}
              />
            ))
          )}
        </div>

        {/* Map and Controls */}
        <div className="lg:col-span-2 space-y-4">
          {selectedMissionData && (
            <>
              {/* Mission Controls */}
              <div className="bg-gray-800 rounded-lg border border-gray-700 p-4">
                <div className="flex items-center justify-between mb-4">
                  <h4 className="font-medium text-white">{selectedMissionData.name}</h4>
                  <div className="flex items-center gap-2">
                    {selectedMissionData.status === 'in-progress' && (
                      <button
                        onClick={() => handleMissionControl(selectedMissionData.id, 'pause')}
                        className="bg-yellow-600 hover:bg-yellow-700 text-white p-2 rounded-md transition-colors"
                      >
                        <Pause className="h-4 w-4" />
                      </button>
                    )}
                    {selectedMissionData.status === 'paused' && (
                      <button
                        onClick={() => handleMissionControl(selectedMissionData.id, 'resume')}
                        className="bg-green-600 hover:bg-green-700 text-white p-2 rounded-md transition-colors"
                      >
                        <Play className="h-4 w-4" />
                      </button>
                    )}
                    <button
                      onClick={() => handleMissionControl(selectedMissionData.id, 'abort')}
                      className="bg-red-600 hover:bg-red-700 text-white p-2 rounded-md transition-colors"
                    >
                      <Square className="h-4 w-4" />
                    </button>
                  </div>
                </div>

                {/* Progress Bar */}
                <div className="mb-4">
                  <div className="flex justify-between text-sm text-gray-400 mb-1">
                    <span>Progress</span>
                    <span>{Math.round(selectedMissionData.progress)}%</span>
                  </div>
                  <div className="w-full bg-gray-700 rounded-full h-2">
                    <div
                      className="bg-blue-500 h-2 rounded-full transition-all duration-300"
                      style={{ width: `${selectedMissionData.progress}%` }}
                    />
                  </div>
                </div>

                {/* Mission Stats */}
                <div className="grid grid-cols-3 gap-4">
                  <div className="text-center">
                    <span className="text-sm font-medium text-medium-contrast mb-1 block">Status</span>
                    <span className="text-lg font-bold text-high-contrast">{selectedMissionData.status}</span>
                  </div>
                  <div className="text-center">
                    <span className="text-sm font-medium text-medium-contrast mb-1 block">Duration</span>
                    <span className="text-lg font-bold text-high-contrast">{selectedMissionData.estimatedDuration}m</span>
                  </div>
                  <div className="text-center">
                    <span className="text-sm font-medium text-medium-contrast mb-1 block">Altitude</span>
                    <span className="text-lg font-bold text-high-contrast">{selectedMissionData.parameters.altitude}m</span>
                  </div>
                </div>
              </div>

              {/* Map */}
              <div className="bg-gray-800 rounded-lg border border-gray-700 p-4">
                <MapView mission={selectedMissionData} drones={drones} />
              </div>
            </>
          )}
        </div>
      </div>
    </div>
  );
};

export default LiveMonitoring;

src/components/monitoring/MapView.tsx.content:
import React from 'react';
import { Mission, Drone } from '../../types';

interface MapViewProps {
  mission: Mission;
  drones: Drone[];
}

const MapView: React.FC<MapViewProps> = ({ mission, drones }) => {
  // Find the drone for this mission
  const missionDrone = drones.find(d => d.id === mission.droneId);

  // Calculate bounds for the map view
  const bounds = mission.area.bounds;
  const minLat = Math.min(...bounds.map(p => p.lat));
  const maxLat = Math.max(...bounds.map(p => p.lat));
  const minLng = Math.min(...bounds.map(p => p.lng));
  const maxLng = Math.max(...bounds.map(p => p.lng));

  const centerLat = (minLat + maxLat) / 2;
  const centerLng = (minLng + maxLng) / 2;

  // Calculate current drone position based on mission progress
  const progressIndex = Math.floor((mission.progress / 100) * mission.flightPath.length);
  const currentWaypoint = mission.flightPath[Math.min(progressIndex, mission.flightPath.length - 1)];

  return (
    <div className="h-96 bg-gray-900 rounded-lg relative overflow-hidden">
      <div className="absolute inset-0 bg-gradient-to-br from-blue-900/20 to-purple-900/20" />

      {/* Map Content */}
      <div className="absolute inset-0 p-4">
        <div className="text-white text-sm mb-4">
          <h4 className="font-medium mb-2">{mission.area.name}</h4>
          <p className="text-gray-400 text-xs">
            Center: {centerLat.toFixed(4)}, {centerLng.toFixed(4)}
          </p>
        </div>

        {/* Flight Path Visualization */}
        <div className="bg-gray-800/80 rounded-lg p-4 mb-4">
          <h5 className="text-white text-sm font-medium mb-2">Flight Path</h5>
          <div className="grid grid-cols-2 gap-4 text-xs">
            <div>
              <span className="text-gray-400 block">Pattern</span>
              <span className="text-white capitalize">{mission.pattern}</span>
            </div>
            <div>
              <span className="text-gray-400 block">Waypoints</span>
              <span className="text-white">{mission.flightPath.length}</span>
            </div>
            <div>
              <span className="text-gray-400 block">Current Position</span>
              <span className="text-white">
                {currentWaypoint.lat.toFixed(4)}, {currentWaypoint.lng.toFixed(4)}
              </span>
            </div>
            <div>
              <span className="text-gray-400 block">Altitude</span>
              <span className="text-white">{currentWaypoint.altitude}m</span>
            </div>
          </div>
        </div>

        {/* Drone Status */}
        {missionDrone && (
          <div className="bg-gray-800/80 rounded-lg p-4">
            <h5 className="text-white text-sm font-medium mb-2">Drone Status</h5>
            <div className="grid grid-cols-2 gap-4 text-xs">
              <div>
                <span className="text-gray-400 block">Drone</span>
                <span className="text-white">{missionDrone.name}</span>
              </div>
              <div>
                <span className="text-gray-400 block">Battery</span>
                <span className={`font-medium ${
                  missionDrone.batteryLevel > 60 ? 'text-green-400' :
                  missionDrone.batteryLevel > 30 ? 'text-yellow-400' : 'text-red-400'
                }`}>
                  {missionDrone.batteryLevel}%
                </span>
              </div>
              <div>
                <span className="text-gray-400 block">Speed</span>
                <span className="text-white">{mission.parameters.speed} m/s</span>
              </div>
              <div>
                <span className="text-gray-400 block">ETA</span>
                <span className="text-white">
                  {Math.max(0, Math.round(mission.estimatedDuration * (1 - mission.progress / 100)))}m
                </span>
              </div>
            </div>
          </div>
        )}

        {/* Visual indicator of drone position */}
        <div className="absolute top-1/2 left-1/2 transform -translate-x-1/2 -translate-y-1/2">
          <div className="w-4 h-4 bg-blue-500 rounded-full animate-pulse shadow-lg">
            <div className="absolute inset-0 bg-blue-400 rounded-full animate-ping opacity-75" />
          </div>
          <div className="text-xs text-white mt-1 text-center">Drone</div>
        </div>
      </div>
    </div>
  );
};

export default MapView;

src/components/monitoring/MissionCard.ts.content:
import React from 'react';
import { Mission } from '../../types';
import { Play, Pause, Square, MapPin, Clock, Activity } from 'lucide-react';

interface MissionCardProps {
  mission: Mission;
  isSelected: boolean;
  onSelect: () => void;
  onControl: (missionId: string, action: 'pause' | 'resume' | 'abort') => void;
}

const MissionCard: React.FC<MissionCardProps> = ({ mission, isSelected, onSelect, onControl }) => {
  const getStatusColor = (status: Mission['status']) => {
    switch (status) {
      case 'in-progress': return 'text-green-400 bg-green-100';
      case 'paused': return 'text-yellow-400 bg-yellow-100';
      case 'completed': return 'text-blue-400 bg-blue-100';
      case 'aborted': return 'text-red-400 bg-red-100';
      default: return 'text-gray-400 bg-gray-100';
    }
  };

  return (
    <div
      className={`bg-gray-800 rounded-lg border p-4 cursor-pointer transition-all duration-200 hover:shadow-lg ${
        isSelected ? 'border-blue-500 ring-1 ring-blue-500' : 'border-gray-700 hover:border-gray-600'
      }`}
      onClick={onSelect}
    >
      <div className="flex items-center justify-between mb-3">
        <h4 className="font-medium text-white text-sm">{mission.name}</h4>
        <span className={`inline-flex items-center px-2 py-1 rounded-full text-xs font-medium ${getStatusColor(mission.status)}`}>
          {mission.status === 'in-progress' && <Activity className="h-3 w-3 mr-1" />}
          {mission.status === 'paused' && <Pause className="h-3 w-3 mr-1" />}
          {mission.status.replace('-', ' ').toUpperCase()}
        </span>
      </div>

      <div className="space-y-2 text-xs">
        <div className="flex items-center justify-between">
          <span className="text-gray-400 flex items-center gap-1">
            <MapPin className="h-3 w-3" />
            Area
          </span>
          <span className="text-white">{mission.area.name}</span>
        </div>

        <div className="flex items-center justify-between">
          <span className="text-gray-400 flex items-center gap-1">
            <Clock className="h-3 w-3" />
            Progress
          </span>
          <span className="text-white">{Math.round(mission.progress)}%</span>
        </div>

        <div className="w-full bg-gray-700 rounded-full h-1.5 mt-2">
          <div
            className="bg-blue-500 h-1.5 rounded-full transition-all duration-300"
            style={{ width: `${mission.progress}%` }}
          />
        </div>

        {mission.stats && (
          <div className="pt-2 mt-2 border-t border-gray-700">
            <div className="grid grid-cols-2 gap-2 text-xs">
              <div>
                <span className="text-gray-400 block">Distance</span>
                <span className="text-white">{mission.stats.distanceCovered} km</span>
              </div>
              <div>
                <span className="text-gray-400 block">Data Points</span>
                <span className="text-white">{mission.stats.dataPointsCollected}</span>
              </div>
            </div>
          </div>
        )}
      </div>

      {isSelected && (
        <div className="mt-3 pt-3 border-t border-gray-700">
          <div className="flex gap-2">
            {mission.status === 'in-progress' && (
              <button
                onClick={(e) => {
                  e.stopPropagation();
                  onControl(mission.id, 'pause');
                }}
                className="flex-1 bg-yellow-600 hover:bg-yellow-700 text-white px-2 py-1 rounded text-xs font-medium transition-colors"
              >
                Pause
              </button>
            )}
            {mission.status === 'paused' && (
              <button
                onClick={(e) => {
                  e.stopPropagation();
                  onControl(mission.id, 'resume');
                }}
                className="flex-1 bg-green-600 hover:bg-green-700 text-white px-2 py-1 rounded text-xs font-medium transition-colors"
              >
                Resume
              </button>
            )}
            <button
              onClick={(e) => {
                e.stopPropagation();
                onControl(mission.id, 'abort');
              }}
              className="flex-1 bg-red-600 hover:bg-red-700 text-white px-2 py-1 rounded text-xs font-medium transition-colors"
            >
              Abort
            </button>
          </div>
        </div>
      )}
    </div>
  );
};

export default MissionCard;

src/components/planning/MissionForm.tsx.content:
import React, { useState } from 'react';
import { Mission, Drone } from '../../types';
import { MapPin, Settings, Calendar, Zap } from 'lucide-react';

interface MissionFormProps {
  drones: Drone[];
  onSubmit: (mission: Omit<Mission, 'id' | 'createdAt'>) => void;
  onCancel: () => void;
}

const MissionForm: React.FC<MissionFormProps> = ({ drones, onSubmit, onCancel }) => {
  const [formData, setFormData] = useState({
    name: '',
    type: 'inspection' as Mission['type'],
    droneId: '',
    areaName: '',
    pattern: 'crosshatch' as Mission['pattern'],
    altitude: 75,
    speed: 6,
    overlapPercentage: 70,
    captureFrequency: 1,
    estimatedDuration: 30
  });

  const [activeStep, setActiveStep] = useState(1);

  const handleSubmit = (e: React.FormEvent) => {
    e.preventDefault();

    // Generate sample flight path based on pattern
    const flightPath = generateFlightPath(formData.pattern, formData.altitude);
    const area = {
      name: formData.areaName,
      bounds: [
        { lat: 37.7749, lng: -122.4194 },
        { lat: 37.7759, lng: -122.4184 },
        { lat: 37.7769, lng: -122.4204 },
        { lat: 37.7759, lng: -122.4214 }
      ]
    };

    const mission: Omit<Mission, 'id' | 'createdAt'> = {
      name: formData.name,
      type: formData.type,
      status: 'planned',
      droneId: formData.droneId,
      area,
      flightPath,
      pattern: formData.pattern,
      parameters: {
        altitude: formData.altitude,
        speed: formData.speed,
        overlapPercentage: formData.overlapPercentage,
        captureFrequency: formData.captureFrequency
      },
      progress: 0,
      estimatedDuration: formData.estimatedDuration
    };

    onSubmit(mission);
  };

  const generateFlightPath = (pattern: string, altitude: number) => {
    // Generate sample waypoints based on pattern
    const basePoints = [
      { lat: 37.7749, lng: -122.4194, altitude },
      { lat: 37.7759, lng: -122.4184, altitude },
      { lat: 37.7769, lng: -122.4204, altitude },
      { lat: 37.7759, lng: -122.4214, altitude }
    ];

    if (pattern === 'crosshatch') {
      return [
        ...basePoints,
        { lat: 37.7754, lng: -122.4199, altitude },
        { lat: 37.7764, lng: -122.4189, altitude },
        { lat: 37.7774, lng: -122.4209, altitude }
      ];
    }

    return basePoints;
  };

  const steps = [
    { number: 1, title: 'Basic Info', icon: Calendar },
    { number: 2, title: 'Area & Pattern', icon: MapPin },
    { number: 3, title: 'Parameters', icon: Settings },
    { number: 4, title: 'Review', icon: Zap }
  ];

  return (
    <div className="bg-gray-800 rounded-lg border border-gray-700 p-6">
      <div className="mb-6">
        <h3 className="text-xl font-semibold text-white mb-4">Create New Mission</h3>

        {/* Progress Steps */}
        <div className="flex items-center justify-between mb-8">
          {steps.map((step) => {
            const Icon = step.icon;
            return (
              <div key={step.number} className="flex items-center">
                <div className={`flex items-center justify-center w-10 h-10 rounded-full border-2 ${
                  activeStep >= step.number
                    ? 'bg-blue-600 border-blue-600 text-white'
                    : 'border-gray-600 text-gray-400'
                }`}>
                  <Icon className="h-5 w-5" />
                </div>
                <span className={`ml-2 text-sm font-medium ${
                  activeStep >= step.number ? 'text-white' : 'text-gray-400'
                }`}>
                  {step.title}
                </span>
                {step.number < steps.length && (
                  <div className={`ml-4 w-12 h-0.5 ${
                    activeStep > step.number ? 'bg-blue-600' : 'bg-gray-600'
                  }`} />
                )}
              </div>
            );
          })}
        </div>
      </div>

      <form onSubmit={handleSubmit} className="space-y-6">
        {/* Step 1: Basic Info */}
        {activeStep === 1 && (
          <div className="space-y-4">
            <div>
              <label className="block text-sm font-medium text-gray-300 mb-2">
                Mission Name
              </label>
              <input
                type="text"
                value={formData.name}
                onChange={(e) => setFormData({ ...formData, name: e.target.value })}
                className="w-full px-3 py-2 bg-gray-700 border border-gray-600 rounded-md text-white focus:outline-none focus:ring-2 focus:ring-blue-500"
                placeholder="Enter mission name"
                required
              />
            </div>

            <div>
              <label className="block text-sm font-medium text-gray-300 mb-2">
                Mission Type
              </label>
              <select
                value={formData.type}
                onChange={(e) => setFormData({ ...formData, type: e.target.value as Mission['type'] })}
                className="w-full px-3 py-2 bg-gray-700 border border-gray-600 rounded-md text-white focus:outline-none focus:ring-2 focus:ring-blue-500"
              >
                <option value="inspection">Infrastructure Inspection</option>
                <option value="security">Security Patrol</option>
                <option value="mapping">Site Mapping</option>
              </select>
            </div>

            <div>
              <label className="block text-sm font-medium text-gray-300 mb-2">
                Select Drone
              </label>
              <select
                value={formData.droneId}
                onChange={(e) => setFormData({ ...formData, droneId: e.target.value })}
                className="w-full px-3 py-2 bg-gray-700 border border-gray-600 rounded-md text-white focus:outline-none focus:ring-2 focus:ring-blue-500"
                required
              >
                <option value="">Select a drone</option>
                {drones.map((drone) => (
                  <option key={drone.id} value={drone.id}>
                    {drone.name} - {drone.model} (Battery: {drone.batteryLevel}%)
                  </option>
                ))}
              </select>
            </div>
          </div>
        )}

        {/* Step 2: Area & Pattern */}
        {activeStep === 2 && (
          <div className="space-y-4">
            <div>
              <label className="block text-sm font-medium text-gray-300 mb-2">
                Survey Area Name
              </label>
              <input
                type="text"
                value={formData.areaName}
                onChange={(e) => setFormData({ ...formData, areaName: e.target.value })}
                className="w-full px-3 py-2 bg-gray-700 border border-gray-600 rounded-md text-white focus:outline-none focus:ring-2 focus:ring-blue-500"
                placeholder="Enter area name"
                required
              />
            </div>

            <div>
              <label className="block text-sm font-medium text-gray-300 mb-2">
                Flight Pattern
              </label>
              <div className="grid grid-cols-1 sm:grid-cols-3 gap-3">
                {[
                  { value: 'crosshatch', label: 'Crosshatch', description: 'Grid pattern for comprehensive coverage' },
                  { value: 'perimeter', label: 'Perimeter', description: 'Follow boundary edges' },
                  { value: 'custom', label: 'Custom', description: 'Define custom waypoints' }
                ].map((pattern) => (
                  <div
                    key={pattern.value}
                    className={`p-3 border rounded-lg cursor-pointer transition-colors ${
                      formData.pattern === pattern.value
                        ? 'border-blue-500 bg-blue-500/10'
                        : 'border-gray-600 hover:border-gray-500'
                    }`}
                    onClick={() => setFormData({ ...formData, pattern: pattern.value as Mission['pattern'] })}
                  >
                    <h4 className="font-medium text-white text-sm">{pattern.label}</h4>
                    <p className="text-gray-400 text-xs mt-1">{pattern.description}</p>
                  </div>
                ))}
              </div>
            </div>
          </div>
        )}

        {/* Step 3: Parameters */}
        {activeStep === 3 && (
          <div className="grid grid-cols-1 sm:grid-cols-2 gap-4">
            <div>
              <label className="block text-sm font-medium text-gray-300 mb-2">
                Flight Altitude (m)
              </label>
              <input
                type="number"
                value={formData.altitude}
                onChange={(e) => setFormData({ ...formData, altitude: parseInt(e.target.value) })}
                className="w-full px-3 py-2 bg-gray-700 border border-gray-600 rounded-md text-white focus:outline-none focus:ring-2 focus:ring-blue-500"
                min="10"
                max="400"
              />
            </div>

            <div>
              <label className="block text-sm font-medium text-gray-300 mb-2">
                Flight Speed (m/s)
              </label>
              <input
                type="number"
                value={formData.speed}
                onChange={(e) => setFormData({ ...formData, speed: parseInt(e.target.value) })}
                className="w-full px-3 py-2 bg-gray-700 border border-gray-600 rounded-md text-white focus:outline-none focus:ring-2 focus:ring-blue-500"
                min="1"
                max="15"
              />
            </div>

            <div>
              <label className="block text-sm font-medium text-gray-300 mb-2">
                Overlap Percentage (%)
              </label>
              <input
                type="number"
                value={formData.overlapPercentage}
                onChange={(e) => setFormData({ ...formData, overlapPercentage: parseInt(e.target.value) })}
                className="w-full px-3 py-2 bg-gray-700 border border-gray-600 rounded-md text-white focus:outline-none focus:ring-2 focus:ring-blue-500"
                min="50"
                max="90"
              />
            </div>

            <div>
              <label className="block text-sm font-medium text-gray-300 mb-2">
                Capture Frequency (sec)
              </label>
              <input
                type="number"
                value={formData.captureFrequency}
                onChange={(e) => setFormData({ ...formData, captureFrequency: parseInt(e.target.value) })}
                className="w-full px-3 py-2 bg-gray-700 border border-gray-600 rounded-md text-white focus:outline-none focus:ring-2 focus:ring-blue-500"
                min="0.5"
                max="10"
                step="0.5"
              />
            </div>

            <div className="sm:col-span-2">
              <label className="block text-sm font-medium text-gray-300 mb-2">
                Estimated Duration (minutes)
              </label>
              <input
                type="number"
                value={formData.estimatedDuration}
                onChange={(e) => setFormData({ ...formData, estimatedDuration: parseInt(e.target.value) })}
                className="w-full px-3 py-2 bg-gray-700 border border-gray-600 rounded-md text-white focus:outline-none focus:ring-2 focus:ring-blue-500"
                min="5"
                max="120"
              />
            </div>
          </div>
        )}

        {/* Step 4: Review */}
        {activeStep === 4 && (
          <div className="bg-gray-900 rounded-lg p-4">
            <h4 className="font-medium text-white mb-4">Mission Review</h4>
            <div className="grid grid-cols-1 sm:grid-cols-2 gap-4 text-sm">
              <div>
                <span className="text-gray-400 block">Name:</span>
                <span className="text-white">{formData.name}</span>
              </div>
              <div>
                <span className="text-gray-400 block">Type:</span>
                <span className="text-white capitalize">{formData.type}</span>
              </div>
              <div>
                <span className="text-gray-400 block">Drone:</span>
                <span className="text-white">
                  {drones.find(d => d.id === formData.droneId)?.name || 'Not selected'}
                </span>
              </div>
              <div>
                <span className="text-gray-400 block">Pattern:</span>
                <span className="text-white capitalize">{formData.pattern}</span>
              </div>
              <div>
                <span className="text-gray-400 block">Altitude:</span>
                <span className="text-white">{formData.altitude}m</span>
              </div>
              <div>
                <span className="text-gray-400 block">Duration:</span>
                <span className="text-white">{formData.estimatedDuration} minutes</span>
              </div>
            </div>
          </div>
        )}

        {/* Navigation Buttons */}
        <div className="flex justify-between pt-6 border-t border-gray-700">
          <div className="flex gap-2">
            <button
              type="button"
              onClick={onCancel}
              className="px-4 py-2 text-gray-400 hover:text-white transition-colors"
            >
              Cancel
            </button>
            {activeStep > 1 && (
              <button
                type="button"
                onClick={() => setActiveStep(activeStep - 1)}
                className="px-4 py-2 bg-gray-700 hover:bg-gray-600 text-white rounded-md transition-colors"
              >
                Back
              </button>
            )}
          </div>

          <div>
            {activeStep < 4 ? (
              <button
                type="button"
                onClick={() => setActiveStep(activeStep + 1)}
                className="px-4 py-2 bg-blue-600 hover:bg-blue-700 text-white rounded-md transition-colors"
                disabled={
                  (activeStep === 1 && (!formData.name || !formData.droneId)) ||
                  (activeStep === 2 && !formData.areaName)
                }
              >
                Next
              </button>
            ) : (
              <button
                type="submit"
                className="px-6 py-2 bg-green-600 hover:bg-green-700 text-white rounded-md font-medium transition-colors"
              >
                Create Mission
              </button>
            )}
          </div>
        </div>
      </form>
    </div>
  );
};

export default MissionForm;

src/components/planning/MissionPlanning..content:
import React, { useState } from 'react';
import { useDrone } from '../../contexts/DroneContext';
import MissionForm from './MissionForm';
import { Mission } from '../../types';

const MissionPlanning: React.FC = () => {
  const { drones, addMission } = useDrone();
  const [showForm, setShowForm] = useState(false);

  const availableDrones = drones.filter(drone => drone.status === 'available');

  const handleMissionCreate = (missionData: Omit<Mission, 'id' | 'createdAt'>) => {
    addMission(missionData);
    setShowForm(false);
  };

  return (
    <div className="space-y-6">
      <div className="flex justify-between items-center">
        <h2 className="text-2xl font-bold text-white">Mission Planning</h2>
        <button
          onClick={() => setShowForm(true)}
          className="bg-blue-600 hover:bg-blue-700 text-white px-4 py-2 rounded-md font-medium transition-colors"
        >
          Create New Mission
        </button>
      </div>

      {showForm ? (
        <MissionForm
          drones={availableDrones}
          onSubmit={handleMissionCreate}
          onCancel={() => setShowForm(false)}
        />
      ) : (
        <div className="bg-gray-800 rounded-lg border border-gray-700 p-8 text-center">
          <h3 className="text-xl font-medium text-white mb-4">Plan autonomous drone surveys with precision flight paths and data collection parameters</h3>
          <p className="text-gray-400 mb-6">
            {availableDrones.length} drones available
          </p>
          <div className="grid grid-cols-1 md:grid-cols-3 gap-6 mb-8">
            <div className="card-premium rounded-xl p-6 text-center">
              <h4 className="text-sm font-medium text-medium-contrast mb-2">Survey Patterns</h4>
              <p className="text-high-contrast text-sm">Crosshatch, perimeter, or custom flight paths</p>
            </div>
            <div className="card-premium rounded-xl p-6 text-center">
              <h4 className="text-sm font-medium text-medium-contrast mb-2">Real-time Control</h4>
              <p className="text-high-contrast text-sm">Monitor and control missions as they happen</p>
            </div>
            <div className="card-premium rounded-xl p-6 text-center">
              <h4 className="text-sm font-medium text-medium-contrast mb-2">Detailed Analytics</h4>
              <p className="text-high-contrast text-sm">Comprehensive reporting and data insights</p>
            </div>
          </div>
          <button
            onClick={() => setShowForm(true)}
            className="bg-blue-600 hover:bg-blue-700 text-white px-6 py-3 rounded-md font-medium transition-colors"
          >
            Start Planning
          </button>
        </div>
      )}
    </div>
  );
};

export default MissionPlanning;

src/components/reporting/AnalyticsCharts.content:
import React from 'react';
import { Mission } from '../../types';
import { BarChart, Bar, XAxis, YAxis, CartesianGrid, Tooltip, ResponsiveContainer, PieChart, Pie, Cell, LineChart, Line } from 'recharts';

interface AnalyticsChartsProps {
  missions: Mission[];
}

const AnalyticsCharts: React.FC<AnalyticsChartsProps> = ({ missions }) => {
  // Mission types data
  const typeData = ['inspection', 'security', 'mapping'].map(type => ({
    name: type.charAt(0).toUpperCase() + type.slice(1),
    count: missions.filter(m => m.type === type).length,
    color: type === 'inspection' ? '#3B82F6' : type === 'security' ? '#10B981' : '#8B5CF6'
  }));

  // Monthly missions data
  const monthlyData = React.useMemo(() => {
    const monthNames = ['Jan', 'Feb', 'Mar', 'Apr', 'May', 'Jun'];
    return monthNames.map((month, index) => ({
      month,
      missions: Math.floor(Math.random() * 20) + 10,
      flightTime: Math.floor(Math.random() * 50) + 30
    }));
  }, []);

  // Flight duration distribution
  const durationData = React.useMemo(() => {
    const ranges = ['0-15min', '15-30min', '30-45min', '45-60min', '60min+'];
    return ranges.map(range => ({
      range,
      count: Math.floor(Math.random() * 15) + 5
    }));
  }, []);

  // Success rate data
  const successData = React.useMemo(() => {
    const total = missions.length;
    const completed = missions.filter(m => m.status === 'completed').length;
    const aborted = missions.filter(m => m.status === 'aborted').length;
    const inProgress = total - completed - aborted;

    return [
      { name: 'Completed', value: completed, color: '#10B981' },
      { name: 'In Progress', value: inProgress, color: '#F59E0B' },
      { name: 'Aborted', value: aborted, color: '#EF4444' }
    ];
  }, [missions]);

  return (
    <div className="space-y-6">
      {/* Charts Grid */}
      <div className="grid grid-cols-1 lg:grid-cols-2 gap-6">
        {/* Mission Types Chart */}
        <div className="bg-gray-800 rounded-lg border border-gray-700 p-6">
          <h3 className="text-lg font-medium text-white mb-4">Mission Types Distribution</h3>
          <ResponsiveContainer width="100%" height={300}>
            <BarChart data={typeData}>
              <CartesianGrid strokeDasharray="3 3" stroke="#374151" />
              <XAxis dataKey="name" tick={{ fill: '#9CA3AF' }} />
              <YAxis tick={{ fill: '#9CA3AF' }} />
              <Tooltip
                contentStyle={{
                  backgroundColor: '#1F2937',
                  border: '1px solid #374151',
                  borderRadius: '0.5rem',
                  color: '#F3F4F6'
                }}
              />
              <Bar dataKey="count" fill="#3B82F6" radius={[4, 4, 0, 0]} />
            </BarChart>
          </ResponsiveContainer>
        </div>

        {/* Success Rate Pie Chart */}
        <div className="bg-gray-800 rounded-lg border border-gray-700 p-6">
          <h3 className="text-lg font-medium text-white mb-4">Mission Success Rate</h3>
          <ResponsiveContainer width="100%" height={300}>
            <PieChart>
              <Pie
                data={successData}
                cx="50%"
                cy="50%"
                outerRadius={100}
                dataKey="value"
                label={({ name, percent }) => `${name} ${(percent * 100).toFixed(0)}%`}
              >
                {successData.map((entry, index) => (
                  <Cell key={index} fill={entry.color} />
                ))}
              </Pie>
              <Tooltip
                contentStyle={{
                  backgroundColor: '#1F2937',
                  border: '1px solid #374151',
                  borderRadius: '0.5rem',
                  color: '#F3F4F6'
                }}
              />
            </PieChart>
          </ResponsiveContainer>
        </div>

        {/* Monthly Trends */}
        <div className="bg-gray-800 rounded-lg border border-gray-700 p-6">
          <h3 className="text-lg font-medium text-white mb-4">Monthly Mission Trends</h3>
          <ResponsiveContainer width="100%" height={300}>
            <LineChart data={monthlyData}>
              <CartesianGrid strokeDasharray="3 3" stroke="#374151" />
              <XAxis dataKey="month" tick={{ fill: '#9CA3AF' }} />
              <YAxis tick={{ fill: '#9CA3AF' }} />
              <Tooltip
                contentStyle={{
                  backgroundColor: '#1F2937',
                  border: '1px solid #374151',
                  borderRadius: '0.5rem',
                  color: '#F3F4F6'
                }}
              />
              <Line
                type="monotone"
                dataKey="missions"
                stroke="#3B82F6"
                strokeWidth={2}
                dot={{ fill: '#3B82F6', r: 4 }}
                name="Missions"
              />
              <Line
                type="monotone"
                dataKey="flightTime"
                stroke="#10B981"
                strokeWidth={2}
                dot={{ fill: '#10B981', r: 4 }}
                name="Flight Hours"
              />
            </LineChart>
          </ResponsiveContainer>
        </div>

        {/* Duration Distribution */}
        <div className="bg-gray-800 rounded-lg border border-gray-700 p-6">
          <h3 className="text-lg font-medium text-white mb-4">Flight Duration Distribution</h3>
          <ResponsiveContainer width="100%" height={300}>
            <BarChart data={durationData} layout="horizontal">
              <CartesianGrid strokeDasharray="3 3" stroke="#374151" />
              <XAxis type="number" tick={{ fill: '#9CA3AF' }} />
              <YAxis dataKey="range" type="category" tick={{ fill: '#9CA3AF' }} width={80} />
              <Tooltip
                contentStyle={{
                  backgroundColor: '#1F2937',
                  border: '1px solid #374151',
                  borderRadius: '0.5rem',
                  color: '#F3F4F6'
                }}
              />
              <Bar dataKey="count" fill="#8B5CF6" radius={[0, 4, 4, 0]} />
            </BarChart>
          </ResponsiveContainer>
        </div>
      </div>

      {/* Summary Statistics */}
      <div className="grid grid-cols-1 md:grid-cols-4 gap-6">
        <div className="bg-gray-800 rounded-lg border border-gray-700 p-6 text-center">
          <h4 className="text-sm font-medium text-gray-400 mb-2">Average Mission Duration</h4>
          <p className="text-2xl font-bold text-white">
            {missions.length > 0 
              ? Math.round(missions.reduce((sum, m) => sum + (m.actualDuration || m.estimatedDuration), 0) / missions.length)
              : 0
            } min
          </p>
        </div>

        <div className="bg-gray-800 rounded-lg border border-gray-700 p-6 text-center">
          <h4 className="text-sm font-medium text-gray-400 mb-2">Success Rate</h4>
          <p className="text-2xl font-bold text-green-400">
            {missions.length > 0
              ? Math.round((missions.filter(m => m.status === 'completed').length / missions.length) * 100)
              : 0
            }%
          </p>
        </div>

        <div className="bg-gray-800 rounded-lg border border-gray-700 p-6 text-center">
          <h4 className="text-sm font-medium text-gray-400 mb-2">Total Distance</h4>
          <p className="text-2xl font-bold text-blue-400">
            {missions
              .filter(m => m.stats)
              .reduce((sum, m) => sum + (m.stats?.distanceCovered || 0), 0)
              .toFixed(1)} km
          </p>
        </div>

        <div className="bg-gray-800 rounded-lg border border-gray-700 p-6 text-center">
          <h4 className="text-sm font-medium text-gray-400 mb-2">Data Points Collected</h4>
          <p className="text-2xl font-bold text-purple-400">
            {missions
              .filter(m => m.stats)
              .reduce((sum, m) => sum + (m.stats?.dataPointsCollected || 0), 0)
              .toLocaleString()}
          </p>
        </div>
      </div>
    </div>
  );
};

export default AnalyticsCharts;

src/components/reporting/MissionHistory..content:
import React, { useState } from 'react';
import { Mission } from '../../types';
import { Search, Filter, Calendar, MapPin, Clock, BarChart } from 'lucide-react';

interface MissionHistoryProps {
  missions: Mission[];
}

const MissionHistory: React.FC<MissionHistoryProps> = ({ missions }) => {
  const [searchTerm, setSearchTerm] = useState('');
  const [statusFilter, setStatusFilter] = useState('all');
  const [typeFilter, setTypeFilter] = useState('all');

  const filteredMissions = missions.filter(mission => {
    const matchesSearch = mission.name.toLowerCase().includes(searchTerm.toLowerCase()) ||
                         mission.area.name.toLowerCase().includes(searchTerm.toLowerCase());
    const matchesStatus = statusFilter === 'all' || mission.status === statusFilter;
    const matchesType = typeFilter === 'all' || mission.type === typeFilter;

    return matchesSearch && matchesStatus && matchesType;
  });

  const getStatusColor = (status: Mission['status']) => {
    switch (status) {
      case 'completed': return 'bg-green-100 text-green-800';
      case 'in-progress': return 'bg-blue-100 text-blue-800';
      case 'aborted': return 'bg-red-100 text-red-800';
      case 'paused': return 'bg-yellow-100 text-yellow-800';
      default: return 'bg-gray-100 text-gray-800';
    }
  };

  const getTypeColor = (type: Mission['type']) => {
    switch (type) {
      case 'inspection': return 'bg-blue-100 text-blue-800';
      case 'security': return 'bg-green-100 text-green-800';
      case 'mapping': return 'bg-purple-100 text-purple-800';
      default: return 'bg-gray-100 text-gray-800';
    }
  };

  return (
    <div className="space-y-6">
      {/* Filters */}
      <div className="bg-gray-800 rounded-lg border border-gray-700 p-4">
        <div className="grid grid-cols-1 md:grid-cols-4 gap-4">
          <div className="relative">
            <Search className="absolute left-3 top-1/2 transform -translate-y-1/2 h-4 w-4 text-gray-400" />
            <input
              type="text"
              placeholder="Search missions..."
              value={searchTerm}
              onChange={(e) => setSearchTerm(e.target.value)}
              className="w-full pl-10 pr-3 py-2 bg-gray-700 border border-gray-600 rounded-md text-white focus:outline-none focus:ring-2 focus:ring-blue-500"
            />
          </div>

          <select
            value={statusFilter}
            onChange={(e) => setStatusFilter(e.target.value)}
            className="w-full px-3 py-2 bg-gray-700 border border-gray-600 rounded-md text-white focus:outline-none focus:ring-2 focus:ring-blue-500"
          >
            <option value="all">All Statuses</option>
            <option value="completed">Completed</option>
            <option value="in-progress">In Progress</option>
            <option value="aborted">Aborted</option>
            <option value="paused">Paused</option>
          </select>

          <select
            value={typeFilter}
            onChange={(e) => setTypeFilter(e.target.value)}
            className="w-full px-3 py-2 bg-gray-700 border border-gray-600 rounded-md text-white focus:outline-none focus:ring-2 focus:ring-blue-500"
          >
            <option value="all">All Types</option>
            <option value="inspection">Inspection</option>
            <option value="security">Security</option>
            <option value="mapping">Mapping</option>
          </select>

          <div className="text-sm text-gray-400 flex items-center">
            <Filter className="h-4 w-4 mr-1" />
            {filteredMissions.length} of {missions.length} missions
          </div>
        </div>
      </div>

      {/* Mission List */}
      <div className="space-y-4">
        {filteredMissions.map((mission) => (
          <div key={mission.id} className="bg-gray-800 rounded-lg border border-gray-700 p-6 hover:shadow-lg transition-all duration-200 hover:border-blue-500">
            <div className="flex items-center justify-between mb-4">
              <h3 className="text-lg font-medium text-white">{mission.name}</h3>
              <div className="flex items-center gap-2">
                <span className={`inline-flex items-center px-2 py-1 rounded-full text-xs font-medium ${getTypeColor(mission.type)}`}>
                  {mission.type.charAt(0).toUpperCase() + mission.type.slice(1)}
                </span>
                <span className={`inline-flex items-center px-2 py-1 rounded-full text-xs font-medium ${getStatusColor(mission.status)}`}>
