      color: 'text-green-400',
      bgColor: 'bg-green-50'
    },
    {
      title: 'In Mission',
      value: (statusCounts['in-mission'] || 0).toString(),
      icon: Activity,
      color: 'text-orange-400',
      bgColor: 'bg-orange-50'
    },
    {
      title: 'Avg Battery',
      value: `${Math.round(averageBattery)}%`,
